package com.eureka.feign;import org.springframework.boot.SpringApplication;import org.springframework.boot.autoconfigure.SpringBootApplication;import org.springframework.cloud.client.discovery.EnableDiscoveryClient;import org.springframework.cloud.netflix.feign.EnableFeignClients;@SpringBootApplication@EnableDiscoveryClient@EnableFeignClients(basePackages = {"com.eureka.feign"})public class ApplicationFeign {    public static void main(String[] dd){        SpringApplication.run(ApplicationFeign.class,dd);    }}